import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import CloseIcon from '../../images/CloseIcon.svg';
import MenuBarIcon from '../../images/MenuBarIcon.svg';
import styles from './Navbar.module.scss';
import Button from '../button/Button';

const Navbar = () => {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth < 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  window.addEventListener('resize', shou)

  return (
    <div>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to="/" className='navbar-logo'>
                    Home
          </Link>
          <div onClick={handleClick}>
            <img src={ click ? CloseIcon : MenuBarIcon} className={styles['menu-icon']} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                    Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/page-two' className='nav-links' onClick={closeMobileMenu}>
                    Page 2
              </Link>
            </li>
          </ul>
          {button &&
          <Button buttonStyle='btn-outline'>
            im a button
          </Button>}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
