import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CloseIcon from '../../images/CloseIcon.svg';
import MenuBarIcon from '../../images/MenuBarIcon.svg';
import styles from './Navbar.module.scss';
import Button from '../button/Button';

const Navbar = () => {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth < 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <div>
      <nav className={styles.navbar}>
        <div className={styles['navbar-container']}>
          <Link to="/" className={styles['navbar-logo']} onClick={closeMobileMenu}>
            Home
          </Link>
          <div role="button" tabIndex={0} onClick={handleClick} className={styles['menu-icon']}>
            <img src={click ? CloseIcon : MenuBarIcon} alt="close menu icon" />
          </div>
          <ul className={click ? styles['nav-menu active'] : styles['nav-menu']}>
            <li className={styles['nav-item']}>
              <Link to="/" className={styles['nav-links']} onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className={styles['nav-item']}>
              <Link to="/page-two" className={styles['nav-links']} onClick={closeMobileMenu}>
                Page 2
              </Link>
            </li>
          </ul>
          {button
          && (
          <Button buttonStyle="btn--outline">
            BUTTON
          </Button>
          )}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
