/* eslint-disable jsx-a11y/no-static-element-interactions */
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Cancel from '../../images/Cancel.svg';
import WhiteMenuBar from '../../images/WhiteMenuBar.svg';
import styles from './Navbar.module.scss';

const Navbar = () => {
  const [click, setClick] = useState(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth < 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <div>
      <nav className={styles.navbar}>
        <div className={styles['navbar-container']}>
          <Link to="/" className={styles['navbar-logo']} onClick={closeMobileMenu}>
            Home
          </Link>
          <div onClick={handleClick}>
            <img src={click ? Cancel : WhiteMenuBar} alt="close menu icon" className={styles['menu-icon']} />
          </div>
          <ul className={click ? styles['nav-menu active'] : styles['nav-menu']}>
            <li className={styles['nav-item']}>
              <Link to="/" className={styles['nav-links']} onClick={closeMobileMenu}>
                About
              </Link>
            </li>
            <li className={styles['nav-item']}>
              <Link to="/resume" className={styles['nav-links']} onClick={closeMobileMenu}>
                Posts
              </Link>
            </li>
          </ul>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
